const Lightbox=()=>{const zoombtn=document.querySelectorAll(".img-btn"),closebtn=document.querySelectorAll(".close"),containerLightbox=document.querySelector(".lightbox"),containerImage=document.querySelector(".img-show"),image=document.querySelectorAll(".imagen-galeria"),copy=document.querySelector(".copy"),imgLightbox=document.querySelector(".img-lightbox"),back=document.querySelector(".back");zoombtn.forEach(image=>{image.addEventListener("click",()=>{containerLightbox.classList.toggle("lightbox-move"),containerImage.classList.toggle("lightbox-show"),back.classList.toggle("lightbox-back"),addImage(image.getAttribute("src"),image.getAttribute("alt"))})}),closebtn.forEach(btn=>{btn.addEventListener("click",()=>{containerLightbox.classList.toggle("lightbox-move"),containerImage.classList.toggle("lightbox-show"),back.classList.toggle("lightbox-back")})});const addImage=(srcImage,altImage)=>{imgLightbox.src=srcImage,copy.innerHTML=altImage}};$(document).ready((function(){Lightbox(Lightbox)}));